import React, { useState, useRef, useEffect } from 'react';
import Chip from '@mui/material/Chip';
import { 
  Box, 
  TextField, 
  IconButton, 
  Paper, 
  Typography,
  CircularProgress
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ReactTypingEffect from 'react-typing-effect';

function ChatInterface({ messages, onSendMessage, loading }) {
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() && !loading) {
      onSendMessage(input.trim());
      setInput('');
    }
  };

  const getAgentColor = (agent) => {
    const colors = {
      'Sarah': '#FF69B4',    // Hot pink for the team lead
      'Mike': '#4CAF50',     // Green for properties
      'Jessica': '#FFA500',  // Orange for negotiations
      'Emma': '#9C27B0',     // Purple for lifestyle
      'Jack': '#2196F3',     // Blue for location
      'Robert': '#795548'    // Brown for legal
    };
    return colors[agent] || '#666666';
  };

  return (
    <Box sx={{ mt: 'auto', mb: 2 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          height: '400px', 
          mb: 2, 
          p: 2, 
          overflow: 'auto',
          backgroundColor: '#ffffff'
        }}
      >
        {messages.map((message, index) => (
          <Box 
            key={index} 
            className={`message-bubble ${message.type === 'user' ? 'user-message' : 'agent-message'}`}
            sx={{
              ...(message.type === 'agent' && {
                borderLeft: `4px solid ${getAgentColor(message.agent)}`,
              })
            }}
          >
            {message.type === 'agent' ? (
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: getAgentColor(message.agent),
                    display: 'flex',
                    alignItems: 'center',
                    gap: 1
                  }}
                >
                  {message.emoji} {message.agent}
                  {message.messageType === 'handoff' && (
                    <Chip 
                      label="Handoff" 
                      size="small" 
                      sx={{ ml: 1 }}
                      color="secondary"
                    />
                  )}
                </Typography>
              </Box>
            ) : null}
            <Typography sx={{ whiteSpace: 'pre-wrap' }}>
              {message.content}
            </Typography>
          </Box>
        ))}
        {loading && (
          <Box className="message-bubble agent-message">
            <ReactTypingEffect 
              text={["Thinking...", "Analyzing options...", "Processing request..."]}
              speed={50}
              eraseSpeed={50}
              typingDelay={100}
              className="typing-effect"
            />
          </Box>
        )}
        <div ref={messagesEndRef} />
      </Paper>

      <form onSubmit={handleSubmit}>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Ask about properties (e.g., 'I'm looking for a place near University of Texas')"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            disabled={loading}
          />
          <IconButton 
            type="submit" 
            color="primary" 
            disabled={!input.trim() || loading}
          >
            {loading ? <CircularProgress size={24} /> : <SendIcon />}
          </IconButton>
        </Box>
      </form>
    </Box>
  );
}

export default ChatInterface;
