import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline } from '@mui/material';
import ChatInterface from './components/ChatInterface';
import Header from './components/Header';
import ResponseDisplay from './components/ResponseDisplay';

const theme = createTheme({
  palette: {
    primary: { main: '#2196f3' },
    secondary: { main: '#f50057' },
    background: { default: '#f5f5f5' },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentResponse, setCurrentResponse] = useState(null);

  const extractAgentMessages = (agent, fullMessage) => {
    if (typeof fullMessage === 'string') {
      return [fullMessage];
    }

    const steps = [];
    if (typeof fullMessage === 'object') {
      if (fullMessage.initial_search?.message) {
        steps.push(fullMessage.initial_search.message);
      }
      if (fullMessage.analysis?.message) {
        steps.push(fullMessage.analysis.message);
      }
      if (fullMessage.final_recommendations?.message) {
        steps.push(fullMessage.final_recommendations.message);
      }
    }

    return steps.length > 0
      ? steps
      : ['[No readable message content from agent]'];
  };

  const handleSendMessage = async (message) => {
    setLoading(true);
    setMessages(prev => [...prev, { type: 'user', content: message }]);

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();
      setCurrentResponse(data);

      if (Array.isArray(data.conversation)) {
        for (const msg of data.conversation) {
          const displaySteps = extractAgentMessages(msg.agent, msg.message);

          for (const content of displaySteps) {
            setMessages(prev => [...prev, {
              type: 'agent',
              agent: msg.agent,
              content,
              emoji: msg.emoji || '',
              messageType: msg.type || 'info'
            }]);
            await new Promise(resolve => setTimeout(resolve, 800));
          }
        }
      } else {
        setMessages(prev => [...prev, {
          type: 'agent',
          agent: 'Sarah',
          content: 'Sorry, I encountered an error. Please try again.',
          emoji: 'üë±‚Äç‚ôÄÔ∏è',
          messageType: 'error'
        }]);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, {
        type: 'agent',
        agent: 'Sarah',
        content: 'Sorry, I encountered an error. Please try again.',
        emoji: 'üë±‚Äç‚ôÄÔ∏è',
        messageType: 'error'
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ height: '100vh', display: 'flex', flexDirection: 'column', py: 2 }}>
        <Header />
        <ResponseDisplay response={currentResponse} />
        <ChatInterface
          messages={messages}
          onSendMessage={handleSendMessage}
          loading={loading}
        />
      </Container>
    </ThemeProvider>
  );
}

export default App;
