import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline } from '@mui/material';
import ChatInterface from './components/ChatInterface';
import Header from './components/Header';

const theme = createTheme({
  palette: {
    primary: { main: '#2196f3' },
    secondary: { main: '#f50057' },
    background: { default: '#f5f5f5' },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentResponse, setCurrentResponse] = useState(null);

  const handleSendMessage = async (message) => {
    setLoading(true);
    setMessages(prev => [...prev, { type: 'user', content: message }]);

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();
      setCurrentResponse(data);

      if (Array.isArray(data.conversation)) {
        let previousAgent = null;
        
        for (const msg of data.conversation) {
          // Add typing indicator
          setMessages(prev => [...prev, {
            type: 'typing',
            agent: msg.name,
            content: '...',
            emoji: msg.emoji,
            role: msg.role
          }]);

          // Simulate typing time
          const typingTime = Math.min(2000, 500 + Math.random() * 1000);
          await new Promise(resolve => setTimeout(resolve, typingTime));

          // Remove typing indicator
          setMessages(prev => prev.filter(m => m.type !== 'typing'));

          // If it's a new agent speaking, add a handoff effect
          if (previousAgent && previousAgent !== msg.name) {
            setMessages(prev => [...prev.slice(0, -1), {
              ...prev[prev.length - 1],
              messageType: 'handoff',
              handoff: msg.name
            }]);
            await new Promise(resolve => setTimeout(resolve, 1000));
          }

          // Process agent's messages
          const formattedMessages = formatMessageContent(msg);
          
          for (const formattedMsg of formattedMessages) {
            setMessages(prev => [...prev, {
              type: 'agent',
              agent: msg.name,
              content: formattedMsg.content,
              emoji: msg.emoji,
              role: msg.role,
              messageType: msg.type,
              contentType: formattedMsg.type
            }]);

            // Add slight delay between multiple messages from same agent
            await new Promise(resolve => setTimeout(resolve, 800));
          }

          previousAgent = msg.name;
        }
      } else {
        setMessages(prev => [...prev, {
          type: 'agent',
          agent: 'Sarah',
          content: 'Sorry, I encountered an error. Please try again.',
          emoji: 'üë±‚Äç‚ôÄÔ∏è',
          role: 'Team Lead',
          messageType: 'error'
        }]);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, {
        type: 'agent',
        agent: 'Sarah',
        content: 'Sorry, I encountered an error. Please try again.',
        emoji: 'üë±‚Äç‚ôÄÔ∏è',
        messageType: 'error'
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ height: '100vh', display: 'flex', flexDirection: 'column', py: 2 }}>
        <Header />
        <ChatInterface
          messages={messages}
          onSendMessage={handleSendMessage}
          loading={loading}
          sx={{ flexGrow: 1 }}
        />
      </Container>
    </ThemeProvider>
  );
}

export default App;
