import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline } from '@mui/material';
import ChatInterface from './components/ChatInterface';
import Header from './components/Header';
import ResponseDisplay from './components/ResponseDisplay';

const theme = createTheme({
  palette: {
    primary: { main: '#2196f3' },
    secondary: { main: '#f50057' },
    background: { default: '#f5f5f5' },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentResponse, setCurrentResponse] = useState(null);

  const handleSendMessage = async (message) => {
    setLoading(true);
    setMessages(prev => [...prev, { type: 'user', content: message }]);

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();
      setCurrentResponse(data);  // Send full data to ResponseDisplay

      if (data.conversation && Array.isArray(data.conversation)) {
        for (const msg of data.conversation) {
          let displayContent;

          if (typeof msg.message === 'object') {
            // If backend sends summary_message, use it
            if (msg.message.summary_message) {
              displayContent = msg.message.summary_message;
            } else {
              // Fallback: prettified JSON string
              displayContent = JSON.stringify(msg.message, null, 2);
            }
          } else {
            displayContent = msg.message;
          }

          setMessages(prev => [...prev, { 
            type: 'agent',
            agent: msg.agent,
            content: displayContent,
            emoji: msg.emoji || 'ü§ñ',
            messageType: msg.type || 'info'
          }]);

          await new Promise(resolve => setTimeout(resolve, 1000));  // Stagger messages
        }
      } else {
        setMessages(prev => [...prev, { 
          type: 'agent',
          agent: 'Sarah',
          content: 'Sorry, I encountered an error. Please try again.',
          emoji: 'üë±‚Äç‚ôÄÔ∏è',
          messageType: 'error'
        }]);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        type: 'agent',
        agent: 'Sarah',
        content: 'Sorry, I encountered an error. Please try again.',
        emoji: 'üë±‚Äç‚ôÄÔ∏è',
        messageType: 'error'
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ height: '100vh', display: 'flex', flexDirection: 'column', py: 2 }}>
        <Header />
        <ResponseDisplay response={currentResponse} />
        <ChatInterface 
          messages={messages} 
          onSendMessage={handleSendMessage} 
          loading={loading} 
        />
      </Container>
    </ThemeProvider>
  );
}

export default App;
