import React, { useState, useRef, useEffect } from 'react';
import Chip from '@mui/material/Chip';
import { 
  Box, 
  TextField, 
  IconButton, 
  Paper, 
  Typography,
  CircularProgress
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ReactTypingEffect from 'react-typing-effect';

function ChatInterface({ messages, onSendMessage, loading }) {
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() && !loading) {
      onSendMessage(input.trim());
      setInput('');
    }
  };

  const getAgentColor = (agent) => {
    const colors = {
      'Sarah': '#FF69B4',    // Hot pink for the team lead
      'Mike': '#4CAF50',     // Green for properties
      'Jessica': '#FFA500',  // Orange for negotiations
      'Emma': '#9C27B0',     // Purple for lifestyle
      'Jack': '#2196F3',     // Blue for location
      'Robert': '#795548'    // Brown for legal
    };
    return colors[agent] || '#666666';
  };

  const renderAgentMessage = (message) => {
    const isTransition = message.messageType === 'handoff' || message.type === 'typing';
    
    return (
      <Box sx={{ 
        display: 'flex',
        flexDirection: 'column',
        alignItems: message.type === 'user' ? 'flex-end' : 'flex-start',
        width: '100%'
      }}>
        {/* Agent Header */}
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: 1,
          mb: 1 
        }}>
          {/* Agent Avatar */}
          <Box sx={{
            width: 32,
            height: 32,
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: `${getAgentColor(message.agent)}20`,
            color: getAgentColor(message.agent),
            fontSize: '1.2rem'
          }}>
            {message.emoji || 'ðŸ‘¤'}
          </Box>

          {/* Agent Name and Role */}
          <Box>
            <Typography variant="subtitle2" sx={{ color: getAgentColor(message.agent) }}>
              {message.agent}
            </Typography>
            {message.role && (
              <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                {message.role}
              </Typography>
            )}
          </Box>

          {/* Message Type Indicator */}
          {message.messageType && message.messageType !== 'default' && (
            <Chip 
              label={message.messageType}
              size="small"
              sx={{ 
                ml: 1,
                backgroundColor: `${getAgentColor(message.agent)}20`,
                color: getAgentColor(message.agent)
              }}
            />
          )}
        </Box>

        {/* Message Content */}
        <Box sx={{ 
          maxWidth: '80%',
          backgroundColor: message.type === 'user' ? 'primary.main' : 'background.paper',
          color: message.type === 'user' ? 'white' : 'text.primary',
          borderRadius: 2,
          p: 2,
          boxShadow: 1,
          position: 'relative',
          '&::after': isTransition ? {
            content: '""',
            position: 'absolute',
            bottom: '-20px',
            left: '50%',
            transform: 'translateX(-50%)',
            borderLeft: '10px solid transparent',
            borderRight: '10px solid transparent',
            borderTop: '10px solid #fff',
          } : {}
        }}>
          {message.type === 'typing' ? (
            <ReactTypingEffect 
              text={[`${message.agent} is thinking...`, `${message.agent} is analyzing...`]}
              speed={50}
              eraseSpeed={50}
              typingDelay={100}
              className="typing-effect"
            />
          ) : (
            <Typography sx={{ whiteSpace: 'pre-wrap' }}>
              {message.content}
            </Typography>
          )}
        </Box>

        {/* Transition Arrow for Handoffs */}
        {message.messageType === 'handoff' && (
          <Box sx={{ 
            width: '100%',
            display: 'flex',
            justifyContent: 'center',
            my: 2
          }}>
            <Typography 
              variant="body2" 
              sx={{ 
                color: 'text.secondary',
                display: 'flex',
                alignItems: 'center',
                gap: 1
              }}
            >
              â†“ Passing to next agent â†“
            </Typography>
          </Box>
        )}
      </Box>
    );
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      <Paper 
        elevation={3} 
        sx={{ 
          flexGrow: 1,
          mb: 2, 
          p: 2, 
          overflow: 'auto',
          backgroundColor: '#ffffff',
          display: 'flex',
          flexDirection: 'column',
          gap: 2
        }}
      >
        {messages.map((message, index) => (
          <Box 
            key={index} 
            className={`message-bubble ${message.type === 'user' ? 'user-message' : 'agent-message'}`}
            sx={{
              ...(message.type === 'agent' && {
                borderLeft: `4px solid ${getAgentColor(message.agent)}`,
              })
            }}
          >
            {message.type === 'agent' || message.type === 'typing' ? (
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: getAgentColor(message.agent),
                    display: 'flex',
                    alignItems: 'center',
                    gap: 1
                  }}
                >
                  {message.emoji} {message.agent}
                  {message.role && (
                    <Chip 
                      label={message.role}
                      size="small"
                      sx={{ 
                        ml: 1,
                        backgroundColor: `${getAgentColor(message.agent)}20`,
                        color: getAgentColor(message.agent)
                      }}
                    />
                  )}
                  {message.messageType === 'handoff' && (
                    <Chip 
                      label="Handoff"
                      size="small"
                      sx={{ ml: 1 }}
                      color="secondary"
                    />
                  )}
                </Typography>
              </Box>
            ) : null}
            {message.type === 'typing' ? (
              <ReactTypingEffect 
                text={[`${message.agent} is thinking...`, `${message.agent} is analyzing...`]}
                speed={50}
                eraseSpeed={50}
                typingDelay={100}
                className="typing-effect"
              />
            ) : (
              <>
                <Typography 
                  sx={{ 
                    whiteSpace: 'pre-wrap',
                    ...(message.contentType === 'properties' && {
                      backgroundColor: '#f5f5f5',
                      p: 1,
                      borderRadius: 1,
                      mt: 1
                    })
                  }}
                >
                  {message.content}
                </Typography>
                {message.contentType === 'handoff' && (
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center',
                    mt: 1 
                  }}>
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        color: 'text.secondary',
                        fontStyle: 'italic'
                      }}
                    >
                      â†“ Handing off to next agent â†“
                    </Typography>
                  </Box>
                )}
              </>
            )}
          </Box>
        ))}
        {loading && (
          <Box className="message-bubble agent-message">
            <ReactTypingEffect 
              text={["Thinking...", "Analyzing options...", "Processing request..."]}
              speed={50}
              eraseSpeed={50}
              typingDelay={100}
              className="typing-effect"
            />
          </Box>
        )}
        <div ref={messagesEndRef} />
      </Paper>

      <form onSubmit={handleSubmit}>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Ask about properties (e.g., 'I'm looking for a place near University of Texas')"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            disabled={loading}
          />
          <IconButton 
            type="submit" 
            color="primary" 
            disabled={!input.trim() || loading}
          >
            {loading ? <CircularProgress size={24} /> : <SendIcon />}
          </IconButton>
        </Box>
      </form>
    </Box>
  );
}

export default ChatInterface;
