import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline } from '@mui/material';
import ChatInterface from './components/ChatInterface';
import Header from './components/Header';
import ResponseDisplay from './components/ResponseDisplay';

const theme = createTheme({
  palette: {
    primary: { main: '#2196f3' },
    secondary: { main: '#f50057' },
    background: { default: '#f5f5f5' }
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif'
  }
});

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentResponse, setCurrentResponse] = useState(null);

  const extractDisplayMessage = (msg) => {
    if (typeof msg === 'string') return msg;
    if (typeof msg === 'object') {
      return msg.summary_message
        || msg.final_recommendations?.message
        || msg.initial_search?.message
        || msg.analysis?.message
        || JSON.stringify(msg, null, 2);
    }
    return String(msg);
  };

  const handleSendMessage = async (message) => {
    setLoading(true);
    setMessages(prev => [...prev, { type: 'user', content: message }]);

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message })
      });

      const data = await response.json();
      setCurrentResponse(data);

      if (data.conversation && Array.isArray(data.conversation)) {
        for (const msg of data.conversation) {
          const displayContent = extractDisplayMessage(msg.message);

          setMessages(prev => [...prev, {
            type: 'agent',
            agent: msg.agent,
            content: displayContent,
            emoji: msg.emoji,
            messageType: msg.type
          }]);

          await new Promise(resolve => setTimeout(resolve, 800)); // Typing delay
        }
      } else {
        setMessages(prev => [...prev, {
          type: 'agent',
          agent: 'Sarah',
          content: 'Sorry, I encountered an error. Please try again.',
          emoji: 'üë±‚Äç‚ôÄÔ∏è',
          messageType: 'error'
        }]);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, {
        type: 'agent',
        agent: 'Sarah',
        content: 'Sorry, I encountered an error. Please try again.',
        emoji: 'üë±‚Äç‚ôÄÔ∏è',
        messageType: 'error'
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ height: '100vh', display: 'flex', flexDirection: 'column', py: 2 }}>
        <Header />
        <ResponseDisplay response={currentResponse} />
        <ChatInterface
          messages={messages}
          onSendMessage={handleSendMessage}
          loading={loading}
        />
      </Container>
    </ThemeProvider>
  );
}

export default App;
